formElements.rule:
'use strict';
({params, imports}) => {
  const encounter = params.entity;
  const moment = imports.moment;
  const formElement = params.formElement;
  const _ = imports.lodash;
  let visibility = true;
  let value = null;
  let answersToSkip = [];
  let validationErrors = [];

  const now = new Date();
  const hours = String(now.getHours()).padStart(2, '0'); // Get the current hour and pad with leading zero if necessary
  const minutes = String(now.getMinutes()).padStart(2, '0'); // Get the current minutes and pad with leading zero if necessary
  const currentTime = hours + ':' + minutes; 

  value = currentTime;

  return new imports.rulesConfig.FormElementStatus(formElement.uuid, visibility, value, answersToSkip, validationErrors);
};
